styles:
.emoji-input-holder {
  position: relative;
  width: 100%;
}
.emoji-input-holder .eih-smiley {
  position: absolute;
  right: 10px;
  font-size: 30px;
  cursor: pointer;
  outline: none;
}
.emoji-input-holder .eih-smiley:before {
  content: '☺';
  font-size: inherit;
  width: 20px;
  height: 30px;
}
.emoji-input-holder .eih-smiley:hover {
  color: #757575;
}
.emoji-input-holder .eih-smiley:active {
  color: #909090;
}
.emoji-input-holder .eih-smiley:focus {
  color: #000000;
}
.emoji-input-holder .eih-emojis-dialog {
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.2);
  background: #fff;
  border-radius: 3px;
  padding: 5px 15px;
  position: absolute;
  z-index: 2;
  width: 200px;
  height: 210px;
  overflow-y: scroll;
  right: 0;
  font-size: 25px;
  margin-top: 6px;
  visibility: hidden;
}
.emoji-input-holder .eih-emojis-dialog.eih-visible { 
	visibility: visible;
}
.emoji-input-holder .eih-emojis-dialog .eih-emoticons-category {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  cursor: pointer;
  margin-bottom: 20px;
}
.emoji-input-holder .eih-emojis-dialog .eih-emoticons-category > p {
  font-family: sans-serif;
  font-size: 14px;
  flex: 1 100%;
  text-align: left;
  margin: 10px 0;
  border-bottom: 1px solid rgba(142, 142, 142, 0.1);
  padding-bottom: 10px;
}
.emoji-input-holder .eih-emojis-dialog .eih-emoticons-category span.eih-emoticon {
  padding: 0 5px;
  border-radius: 3px;
}
.emoji-input-holder .eih-emojis-dialog .eeih-moticons-category span.eih-emoticon:hover {
  background: rgba(0, 0, 0, 0.1);
}

